def chunks(self):
    """Return an iterator that will yield each chunk as a
    (*chunktype*, *content*) pair.
    """
    while True:
        t, v = self.chunk()
        yield t, v
        if t == b"IEND":
            break

def _bytes_to_values(self, bs, width=None):
    """Convert a packed row of bytes into a row of values.
    Result will be a freshly allocated object,
    not shared with the argument.
    """
    if self.bitdepth == 8:
        return bytearray(bs)
    if self.bitdepth == 16:
        return array("H", struct.unpack("!%dH" % (len(bs) // 2), bs))
    assert self.bitdepth < 8
    if width is None:
        width = self.width
    # Samples per byte
    spb = 8 // self.bitdepth
    out = bytearray()
    mask = 2 ** self.bitdepth - 1
    shifts = [self.bitdepth * i for i in reversed(list(range(spb)))]
    for o in bs:
        out.extend([mask & (o >> i) for i in shifts])
    return out[:width]

def iteridat():
    """Iterator that yields all the ``IDAT`` chunks as strings."""
    while True:
        type, data = self.chunk(lenient=lenient)
        if type == b"IEND":
            # http://www.w3.org/TR/PNG/#11IEND
            break
        if type != b"IDAT":
            continue
        # type == b'IDAT'
        # http://www.w3.org/TR/PNG/#11IDAT
        if self.colormap and not self.plte:
            warnings.warn("PLTE chunk is required before IDAT chunk")
        yield data

