def ram_use():
    """Return the amount of ram used by the daemon"""
    return resource.getrusage(resource.RUSAGE_SELF).ru_maxrss / float(1000)

def __init__(self, mycodo, debug):
    threading.Thread.__init__(self)
    self.logger = logging.getLogger('mycodo.pyro_daemon')
    if debug:
        self.logger.setLevel(logging.DEBUG)
    else:
        self.logger.setLevel(logging.WARNING)
    self.mycodo = mycodo

def output_on_off(self, output_id, state, output_type=None, amount=0.0, output_channel=None):
    """ Turn an output on or off """
    if state in ['on', 1, True]:
        return self.output_on(
            output_id, amount=amount, output_type=output_type, output_channel=output_channel)
    elif state in ['off', 0, False]:
        return self.output_off(output_id, output_channel=output_channel)
    else:
        return 1, 'state not "on", 1, True, "off", 0, or False. Found: "{}"'.format(state)

def admin_dependency_install(device):
    """ Install Dependencies """
    messages = {
        "success": [],
        "info": [],
        "warning": [],
        "error": []
    }
    try:
        device_unmet_dependencies, _ = utils_general.return_dependencies(device)
        with open(DEPENDENCY_INIT_FILE, 'w') as f:
            f.write('1')
        install_deps = threading.Thread(
            target=install_dependencies,
            args=(device_unmet_dependencies,))
        install_deps.start()
        messages["success"].append("Dependency install initiated")
    except Exception as err:
        messages["error"].append("Error: {}".format(err))
    return jsonify(data={
        'messages': messages
    })

