def get_measurement(self):
    """ Gets the DS1822's temperature in Celsius """
    if not self.sensor:
        self.logger.error("Input not set up")
        return
    self.return_dict = copy.deepcopy(measurements_dict)
    temperature = None
    n = 2
    for i in range(n):
        try:
            temperature = self.sensor.get_temperature()
            break
        except Exception as e:
            if i == n:
                self.logger.exception(
                    "{cls} raised an exception when taking a reading: {err}".format(cls=type(self).__name__, err=e))
            time.sleep(1)
    if temperature == 85:
        self.logger.error("Measurement returned 85 C, indicating an issue communicating with the sensor.")
        return None
    elif temperature is not None and -55 > temperature > 125:
        self.logger.error(
            "Measurement outside the expected range of -55 C to 125 C: {temp} C".format(temp=temperature))
        return None
    self.value_set(0, temperature)
    return self.return_dict

def get_measurement(self):
    """ Gets the MH-Z19's CO2 concentration in ppmv """
    if not self.ser:
        self.logger.error("Input not set up")
        return
    self.return_dict = copy.deepcopy(measurements_dict)
    while self.calibrating:
        time.sleep(0.1)
    self.measuring = True
    try:
        self.ser.flushInput()
        self.ser.write(bytearray([0xff, 0x01, 0x86, 0x00, 0x00, 0x00, 0x00, 0x00, 0x79]))
        time.sleep(.01)
        resp = self.ser.read(9)
        if not resp:
            self.logger.debug("No response")
        elif len(resp) < 4:
            self.logger.debug("Too few values in response '{}'".format(resp))
        elif resp[0] != 0xff or resp[1] != 0x86:
            self.logger.error("Bad checksum")
        elif len(resp) >= 4:
            high = resp[2]
            low = resp[3]
            co2 = (high * 256) + low
            self.value_set(0, co2)
    except:
        self.logger.exception("get_measurement()")
    finally:
        self.measuring = False
    return self.return_dict

