def verify_crc(char):
    """Returns the 16-bit CRC of sensor data"""
    crc = 0xFFFF
    for l in char:
        crc = crc ^ l
        for _ in range(1,9):
            if crc & 0x01:
                crc = crc >> 1
                crc = crc ^ 0xA001
            else:
                crc = crc >> 1
    return crc

