def sign_MacOS(contents_dir='.'):
    # Sign everything in MacOS except the main executable
    # which will be signed automatically by codesign when
    # signing the app bundles
    with current_dir(os.path.join(contents_dir, 'MacOS')):
        exe = get_executable('../Info.plist')
        items = {x for x in os.listdir('.') if x != exe and not os.path.islink(x)}
        if items:
            codesign(items)

def __init__(self, term_stream=sys.stdout):
    # Curses isn't available on all platforms
    try:
        import curses
    except:
        return
    # If the stream isn't a tty, then assume it has no capabilities.
    if not hasattr(term_stream, 'isatty') or not term_stream.isatty():
        return
    # Check the terminal type.  If we fail, then assume that the
    # terminal has no capabilities.
    try:
        curses.setupterm()
    except:
        return
    # Look up numeric capabilities.
    self.COLS = curses.tigetnum('cols')
    self.LINES = curses.tigetnum('lines')
    # Look up string capabilities.
    for capability in self._STRING_CAPABILITIES:
        (attrib, cap_name) = capability.split('=')
        setattr(self, attrib, self._escape_code(self._tigetstr(cap_name)))
    # Colors
    set_fg = self._tigetstr('setf')
    if set_fg:
        if not isinstance(set_fg, bytes):
            set_fg = set_fg.encode('utf-8')
        for i,color in zip(range(len(self._COLORS)), self._COLORS):
            setattr(self, color,
                    self._escape_code(curses.tparm((set_fg), i)))
    set_fg_ansi = self._tigetstr('setaf')
    if set_fg_ansi:
        if not isinstance(set_fg_ansi, bytes):
            set_fg_ansi = set_fg_ansi.encode('utf-8')
        for i,color in zip(range(len(self._ANSICOLORS)), self._ANSICOLORS):
            setattr(self, color,
                    self._escape_code(curses.tparm((set_fg_ansi),
                        i)))
    set_bg = self._tigetstr('setb')
    if set_bg:
        if not isinstance(set_bg, bytes):
            set_bg = set_bg.encode('utf-8')
        for i,color in zip(range(len(self._COLORS)), self._COLORS):
            setattr(self, 'BG_'+color,
                    self._escape_code(curses.tparm((set_bg), i)))
    set_bg_ansi = self._tigetstr('setab')
    if set_bg_ansi:
        if not isinstance(set_bg_ansi, bytes):
            set_bg_ansi = set_bg_ansi.encode('utf-8')
        for i,color in zip(range(len(self._ANSICOLORS)), self._ANSICOLORS):
            setattr(self, 'BG_'+color,
                    self._escape_code(curses.tparm((set_bg_ansi),
                        i)))

def _tigetstr(self, cap_name):
    # String capabilities can include "delays" of the form "$<2>".
    # For any modern terminal, we should be able to just ignore
    # these, so strip them out.
    import curses
    if isinstance(cap_name, bytes):
        cap_name = cap_name.decode('utf-8')
    cap = self._escape_code(curses.tigetstr(cap_name))
    return re.sub(r'\$<\d+>[/*]?', '', cap)

def upload_to_fosshub():
    # https://devzone.fosshub.com/dashboard/restApi
    # fosshub has no API to do partial uploads, so we always upload all files.
    api_key = get_fosshub_data()
    def request(path, data=None):
        r = Request('https://api.fosshub.com/rest/' + path.lstrip('/'),
                headers={
                    'Content-Type': 'application/json',
                    'X-auth-key': api_key,
                    'User-Agent': 'calibre'
        })
        res = urlopen(r, data=data)
        ans = json.loads(res.read())
        if ans.get('error'):
            raise SystemExit(ans['error'])
        if res.getcode() != 200:
            raise SystemExit('Request to {} failed with response code: {}'.format(path, res.getcode()))
        # from pprint import pprint
        # pprint(ans)
        return ans['status'] if 'status' in ans else ans['data']
    print('Sending upload request to fosshub...')
    project_id = None
    for project in request('projects'):
        if project['name'].lower() == 'calibre':
            project_id = project['id']
            break
    else:
        raise SystemExit('No calibre project found')
    files = set(installers())
    entries = []
    for fname in files:
        desc = installer_description(fname)
        url = 'https://download.calibre-ebook.com/%s/%s' % (
            __version__, os.path.basename(fname)
        )
        entries.append({
            'fileUrl': url,
            'type': desc,
            'version': __version__,
        })
    jq = {
        'version': __version__,
        'files': entries,
        'publish': True,
        'isOldRelease': False,
    }
    data = json.dumps(jq)
    # print(data)
    data = data.encode('utf-8')
    if not request('projects/{}/releases/'.format(project_id), data=data):
        raise SystemExit('Failed to queue publish job with fosshub')

def convert_entityref(self, name):
    """Convert entity references.
