def is_zipfile(filename):
    """Quickly see if a file is a ZIP file by checking the magic number.
    The filename argument may be a file or file-like object too.
    """
    result = False
    try:
        if hasattr(filename, "read"):
            result = _check_zipfile(filename)
        else:
            with open(filename, "rb") as fp:
                result = _check_zipfile(fp)
    except IOError:
        pass
    return result

def _crc32(self, ch, crc):
    """Compute the CRC32 primitive on one byte."""
    return ((crc >> 8) & 0xffffff) ^ self.crctable[(crc ^ ch) & 0xff]

def read(self, n=-1):
    """Read and return up to n bytes.
    If the argument is omitted, None, or negative, data is read and returned until EOF is reached..
    """
    buf = b''
    if n is None:
        n = -1
    while True:
        if n < 0:
            data = self.read1(n)
        elif n > len(buf):
            data = self.read1(n - len(buf))
        else:
            return buf
        if len(data) == 0:
            return buf
        buf += data

def __init__(self, file, mode="r", compression=ZIP_DEFLATED, allowZip64=True):
    """Open the ZIP file with mode read "r", write "w" or append "a"."""
    if mode not in ("r", "w", "a"):
        raise RuntimeError('ZipFile() requires mode "r", "w", or "a" not %s'%mode)
    if compression == ZIP_STORED:
        pass
    elif compression == ZIP_DEFLATED:
        if not zlib:
            raise RuntimeError(
                  "Compression requires the (missing) zlib module")
    else:
        raise RuntimeError("The compression method %s is not supported" % compression)
    self._allowZip64 = allowZip64
    self._didModify = False
    self.debug = 0  # Level of printing: 0 through 3
    self.NameToInfo = {}    # Find file info given name
    self.filelist = []      # List of ZipInfo instances for archive
    self.compression = compression  # Method of compression
    self.mode = key = mode.replace('b', '')[0]
    self.pwd = None
    self.comment = b''
    # Check if we were passed a file-like object
    if isinstance(file, string_or_bytes):
        self._filePassed = 0
        self.filename = file
        modeDict = {'r' : 'rb', 'w': 'wb', 'a' : 'r+b'}
        try:
            self.fp = open(file, modeDict[mode])
        except IOError:
            if mode == 'a':
                mode = key = 'w'
                self.fp = open(file, modeDict[mode])
            else:
                raise
    else:
        self._filePassed = 1
        self.fp = file
        self.filename = getattr(file, 'name', None)
    if key == 'r':
        self._GetContents()
    elif key == 'w':
        # set the modified flag so central directory gets written
        # even if no files are added to the archive
        self._didModify = True
    elif key == 'a':
        try:
            # See if file is a zip file
            self._RealGetContents()
            self._calculate_file_offsets()
            # seek to start of directory and overwrite
            self.fp.seek(self.start_dir, 0)
        except BadZipfile:
            # file is not a zip file, just append
            self.fp.seek(0, 2)
            # set the modified flag so central directory gets written
            # even if no files are added to the archive
            self._didModify = True
    else:
        if not self._filePassed:
            self.fp.close()
            self.fp = None
        raise RuntimeError('Mode must be "r", "w" or "a"')

def testzip(self):
    """Read all the files and check the CRC."""
    chunk_size = 2 ** 20
    for zinfo in self.filelist:
        try:
            # Read by chunks, to avoid an OverflowError or a
            # MemoryError with very large embedded files.
            f = self.open(zinfo.filename, "r")
            while f.read(chunk_size):     # Check CRC-32
                pass
        except BadZipfile:
            return zinfo.filename

