def macros_only(self, text):
    # fix: hackish
    text = re.sub(r'"\Z', '\" ', text)
    result = []
    for line in re.compile(r'(<.*?>)', re.U).split(text):
        if not re.search(r'<.*>', line):
            rules = []
            if re.search(r'{.+?}', line):
                rules = self.macro_defaults
            for s, r in rules:
                line = s.sub(r, line)
        result.append(line)
    return ''.join(result)

def fTextile(self, match):
