def set_profile(profiles, which):
    attr = which + '_profile'
    sval = getattr(self.opts, attr)
    for x in profiles():
        if x.short_name == sval:
            setattr(self.opts, attr, x)
            return
    self.log.warn(
        'Profile (%s) %r is no longer available, using default'%(which, sval))
    for x in profiles():
        if x.short_name == 'default':
            setattr(self.opts, attr, x)
            break

