def __set__(self, obj, val):
    if not isinstance(val, string_or_bytes):
        val = unicode_type(val)
    if isinstance(val, unicode_type):
        val = val.encode('utf-8')
    if len(val) != self._length:
        raise LRFException("Trying to set fixed_stringfield with a " +
                           "string of  incorrect length")
    obj.pack(val, start=self._start, fmt="<"+unicode_type(len(val))+"s")

def __set__(self, obj, val):
    if not val:
        val = ''
    document = obj.info
    def create_elem():
        elem = document.createElement(self.tag_name)
        parent = document.getElementsByTagName(self.parent)[0]
        parent.appendChild(elem)
        return elem
    if not val:
        val = ''
    if not isinstance(val, unicode_type):
        val = val.decode('utf-8')
    elems = document.getElementsByTagName(self.tag_name)
    elem = None
    if len(elems):
        for candidate in elems:
            if candidate.parentNode.nodeName == self.parent:
                elem = candidate
        if not elem:
            elem = create_elem()
        else:
            elem.normalize()
            while elem.hasChildNodes():
                elem.removeChild(elem.lastChild)
    else:
        elem = create_elem()
    elem.appendChild(document.createTextNode(val))
    obj.info = document

def decorator(f):
    def restore_pos(*args, **kwargs):
        obj = args[0]
        pos = obj._file.tell()
        res = f(*args, **kwargs)
        obj._file.seek(0, 2)
        if obj._file.tell() >= pos:
            obj._file.seek(pos)
        return res
    return restore_pos

def _detect_thumbnail_type(cls, slice):
    """ @param slice: The first 16 bytes of the thumbnail """
    ttype = 0x14  # GIF
    if "PNG" in slice:
        ttype = 0x12
    if "BM" in slice:
        ttype = 0x13
    if "JFIF" in slice:
        ttype = 0x11
    return ttype

