def toLrfContainer(self, lrfWriter, container):
    # find the currentTextStyle
    oldTextStyle = self.findCurrentTextStyle()
    # set the attributes we want changed
    for (name, value) in tuple(iteritems(self.attrs)):
        if name in oldTextStyle.attrs and oldTextStyle.attrs[name] == self.attrs[name]:
            self.attrs.pop(name)
        else:
            container.appendLrfTag(LrfTag(name, value))
    # set a currentTextStyle so nested span can put things back
    oldTextStyle = self.findCurrentTextStyle()
    self.currentTextStyle = oldTextStyle.copy()
    self.currentTextStyle.attrs.update(self.attrs)
    for content in self.contents:
        content.toLrfContainer(lrfWriter, container)
    # put the attributes back the way we found them
    # the attributes persist beyond the next </P>
    # if self.hasFollowingContent():
    for name in self.attrs.keys():
        container.appendLrfTag(LrfTag(name, oldTextStyle.attrs[name]))

def _optimize(tagList, tagName, conversion):
    # copy the tag of interest plus any text
    newTagList = []
    for tag in tagList:
        if tag.name == tagName or tag.name == "rawtext":
            newTagList.append(tag)
    # now, eliminate any duplicates (leaving the last one)
    for i, newTag in enumerate(newTagList[:-1]):
        if newTag.name == tagName and newTagList[i+1].name == tagName:
            tagList.remove(newTag)
    # eliminate redundant settings to same value across text strings
    newTagList = []
    for tag in tagList:
        if tag.name == tagName:
            newTagList.append(tag)
    for i, newTag in enumerate(newTagList[:-1]):
        value = conversion(newTag.parameter)
        nextValue = conversion(newTagList[i+1].parameter)
        if value == nextValue:
            tagList.remove(newTagList[i+1])
    # eliminate any setting that don't have text after them
    while len(tagList) > 0 and tagList[-1].name == tagName:
        del tagList[-1]

def parse_file(self, soup):
    def get_valid_block(page):
        for item in page.contents:
            if isinstance(item, (Canvas, TextBlock, ImageBlock, RuledLine)):
                if isinstance(item, TextBlock) and not item.contents:
                    continue
                return item
    if not self.current_page:
        self.current_page = self.book.create_page()
    self.current_block = self.book.create_text_block()
    self.current_para = Paragraph()
    if self.cover:
        self.add_image_page(self.cover)
        self.cover = None
    top = self.current_block
    self.current_block.must_append = True
    self.soup = soup
    self.process_children(soup, {}, {})
    self.soup = None
    if self.current_para and self.current_block:
        self.current_para.append_to(self.current_block)
    if self.current_block and self.current_page:
        self.current_block.append_to(self.current_page)
    if self.avoid_page_break:
        self.avoid_page_break = False
    elif self.current_page and self.current_page.has_text():
        self.book.append(self.current_page)
        self.current_page = None
    if top not in top.parent.contents:  # May have been removed for a cover image
        top = top.parent.contents[0]
    if not top.has_text() and top.parent.contents.index(top) == len(top.parent.contents)-1:
        # Empty block at the bottom of a page
        opage = top.parent
        top.parent.contents.remove(top)
        if self.book.last_page() is opage:
            if self.current_page and self.current_page.has_text():
                for c in self.current_page.contents:
                    if isinstance(c, (TextBlock, ImageBlock)):
                        return c
            raise ConversionError(_('Could not parse file: %s')%self.file_name)
        else:
            try:
                index = self.book.pages().index(opage)
            except ValueError:
                self.log.warning(_('%s is an empty file')%self.file_name)
                tb = self.book.create_text_block()
                self.current_page.append(tb)
                return tb
            for page in list(self.book.pages()[index+1:]):
                for c in page.contents:
                    if isinstance(c, (TextBlock, ImageBlock, Canvas)):
                        return c
            raise ConversionError(_('Could not parse file: %s')%self.file_name)
    return top

def txt2rtf(text):
    # Escape { and } in the text.
    text = text.replace('{', r'\'7b')
    text = text.replace('}', r'\'7d')
    text = text.replace('\\', r'\'5c')
    if not isinstance(text, unicode_type):
        return text
    buf = io.StringIO()
    for x in text:
        val = ord(x)
        if val == 160:
            buf.write(r'\~')
        elif val <= 127:
            buf.write(x)
        else:
            # python2 and ur'\u' does not work
            c = '\\u{0:d}?'.format(val)
            buf.write(c)
    return buf.getvalue()

def __init__(self, iterable=None):
    self.items = []
    self.map = {}
    if iterable is not None:
        for item in iterable:
            idx = self.map.get(item)
            if idx is None:
                self.map[item] = len(self.items)
                self.items.append(item)

def select_child(cache, left, right):
    """right is an immediate child of left"""
    right = always_in if right is None else frozenset(right)
    for parent in left:
        for child in cache.iterchildren(parent):
            if child in right:
                yield child

def select_indirect_adjacent(cache, left, right):
    """right is a sibling after left, immediately or not"""
    right = always_in if right is None else frozenset(right)
    for parent in left:
        for sibling in cache.itersiblings(parent):
            if sibling in right:
                yield sibling

def select_element(cache, selector):
    """A type or universal selector."""
    element = selector.element
    if not element or element == '*':
        for elem in cache.itertag():
            yield elem
    else:
        for elem in cache.element_map[ascii_lower(element)]:
            yield elem

def select_function(cache, function):
    """Select with a functional pseudo-class."""
    fname = function.name.replace('-', '_')
    try:
        func = cache.dispatch_map[fname]
    except KeyError:
        raise ExpressionError(
            "The pseudo-class :%s() is unknown" % function.name)
    if fname == 'lang':
        items = frozenset(func(cache, function))
        for item in cache.iterparsedselector(function.selector):
            if item in items:
                yield item
    else:
        for item in cache.iterparsedselector(function.selector):
            if func(cache, function, item):
                yield item

def ascii_lower(string):
    """Lower-case, but only in the ASCII range."""
    return string.translate(utab if isinstance(string, unicode_type) else tab)

def line(self):
    """The line number in the CSS source of the first token."""
    return self[0].line

def column(self):
    """The column number (inside a source line) of the first token."""
    return self[0].column

def hue_to_rgb(m1, m2, h):
    if h < 0:
        h += 1
    if h > 1:
        h -= 1
    if h * 6 < 1:
        return m1 + (m2 - m1) * h * 6
    if h * 2 < 1:
        return m2
    if h * 3 < 2:
        return m1 + (m2 - m1) * (2 / 3 - h) * 6
    return m1

def parse_comma_separated(tokens):
    """Parse a list of tokens (typically the content of a function token)
    as arguments made of a single token each, separated by mandatory commas,
    with optional white space around each argument.
    return the argument list without commas or white space;
    or None if the function token content do not match the description above.
    """
    tokens = [token for token in tokens if token.type != 'S']
    if not tokens:
        return []
    if len(tokens) % 2 == 1 and all(
            token.type == 'DELIM' and token.value == ','
            for token in tokens[1::2]):
        return tokens[::2]

def params(css, encoding, use_bom=False, expect_error=False, **kwargs):
    """Nicer syntax to make a tuple."""
    return css, encoding, use_bom, expect_error, kwargs

def jsonify(tokens):
    """Turn tokens into "JSON-compatible" data structures."""
    for token in tokens:
        if token.type == 'FUNCTION':
            yield (token.type, token.function_name,
                   list(jsonify(token.content)))
        elif token.is_container:
            yield token.type, list(jsonify(token.content))
        else:
            yield token.type, token.value

