def get_fileno(obj):
    # The purpose of this function is to exactly replicate
    # the behavior of the select module when confronted with
    # abnormal filenos; the details are extensively tested in
    # the stdlib test/test_select.py.
    try:
        f = obj.fileno
    except AttributeError:
        if not isinstance(obj, six.integer_types):
            raise TypeError("Expected int or long, got %s" % type(obj))
        return obj
    else:
        rv = f()
        if not isinstance(rv, six.integer_types):
            raise TypeError("Expected int or long, got %s" % type(rv))
        return rv

def read(self, *args, **kwargs):
    """Read up to LEN bytes and return them.
    Return zero-length string on EOF."""
    try:
        return self._call_trampolining(
            super(GreenSSLSocket, self).read, *args, **kwargs)
    except IOClosed:
        return b''

def sendto(self, data, addr, flags=0):
    # *NOTE: gross, copied code from ssl.py becase it's not factored well enough to be used as-is
    if self._sslobj:
        raise ValueError("sendto not allowed on instances of %s" %
                         self.__class__)
    else:
        trampoline(self, write=True, timeout_exc=timeout_exc('timed out'))
        return socket.sendto(self, data, addr, flags)

def do_handshake(self):
    """Perform a TLS/SSL handshake."""
    return self._call_trampolining(
        super(GreenSSLSocket, self).do_handshake)

def connect(self, addr):
    """Connects to remote ADDR, and then wraps the connection in
    an SSL channel."""
    # *NOTE: grrrrr copied this code from ssl.py because of the reference
    # to socket.connect which we don't want to call directly
    if self._sslobj:
        raise ValueError("attempt to connect already-connected SSLSocket!")
    self._socket_connect(addr)
    server_side = False
    try:
        sslwrap = _ssl.sslwrap
    except AttributeError:
        # sslwrap was removed in 3.x and later in 2.7.9
        if six.PY2:
            sslobj = self._context._wrap_socket(self._sock, server_side, ssl_sock=self)
        else:
            context = self.context if PY33 else self._context
            sslobj = context._wrap_socket(self, server_side, server_hostname=self.server_hostname)
    else:
        sslobj = sslwrap(self._sock, server_side, self.keyfile, self.certfile,
                         self.cert_reqs, self.ssl_version,
                         self.ca_certs, *self.ciphers)
    try:
        # This is added in Python 3.5, http://bugs.python.org/issue21965
        SSLObject
    except NameError:
        self._sslobj = sslobj
    else:
        if _is_under_py_3_7:
            self._sslobj = SSLObject(sslobj, owner=self)
        else:
            self._sslobj = sslobj
    if self.do_handshake_on_connect:
        self.do_handshake()

def wait(self, timeout=None, check_interval=0.01):
    # Instead of a blocking OS call, this version of wait() uses logic
    # borrowed from the eventlet 0.2 processes.Process.wait() method.
    if timeout is not None:
        endtime = time.time() + timeout
    try:
        while True:
            status = self.poll()
            if status is not None:
                return status
            if timeout is not None and time.time() > endtime:
                raise TimeoutExpired(self.args, timeout)
            eventlet.sleep(check_interval)
    except OSError as e:
        if e.errno == errno.ECHILD:
            # no child process, this happens if the child process
            # already died and has been cleaned up
            return -1
        else:
            raise

def send_response_only(self, code, message=None):
    """Send the response header only."""
    if self.request_version != 'HTTP/0.9':
        if message is None:
            if code in self.responses:
                message = self.responses[code][0]
            else:
                message = ''
        if not hasattr(self, '_headers_buffer'):
            self._headers_buffer = []
        self._headers_buffer.append(("%s %d %s\r\n" %
                (self.protocol_version, code, message)).encode(
                    'latin-1', 'strict'))

def end_headers(self):
    """Send the blank line ending the MIME headers."""
    if self.request_version != 'HTTP/0.9':
        self._headers_buffer.append(b"\r\n")
        self.flush_headers()

def date_time_string(self, timestamp=None):
    """Return the current date and time formatted for a message header."""
    if timestamp is None:
        timestamp = time.time()
    return email.utils.formatdate(timestamp, usegmt=True)

def address_string(self):
    """Return the client address."""
    return self.client_address[0]

def do_GET(self):
    """Serve a GET request."""
    f = self.send_head()
    if f:
        try:
            self.copyfile(f, self.wfile)
        finally:
            f.close()

def nobody_uid():
    """Internal routine to get nobody's uid"""
    global nobody
    if nobody:
        return nobody
    try:
        import pwd
    except ImportError:
        return -1
    try:
        nobody = pwd.getpwnam('nobody')[2]
    except KeyError:
        nobody = 1 + max(x[2] for x in pwd.getpwall())
    return nobody

def do_POST(self):
    """Serve a POST request.
    This is only implemented for CGI scripts.
    """
    if self.is_cgi():
        self.run_cgi()
    else:
        self.send_error(
            HTTPStatus.NOT_IMPLEMENTED,
            "Can only POST to CGI scripts")

def is_executable(self, path):
    """Test whether argument path is an executable file."""
    return executable(path)

def _encode(data, name='data'):
    """Call data.encode("latin-1") but show a better error message."""
    try:
        return data.encode("latin-1")
    except UnicodeEncodeError as err:
        raise UnicodeEncodeError(
            err.encoding,
            err.object,
            err.start,
            err.end,
            "%s (%.20r) is not valid Latin-1. Use %s.encode('utf-8') "
            "if you want to send it encoded in UTF-8." %
            (name.title(), data[err.start:err.end], name)) from None

def readinto(self, b):
    """Read up to len(b) bytes into bytearray b and return the number
    of bytes read.
    """
    if self.fp is None:
        return 0
    if self._method == "HEAD":
        self._close_conn()
        return 0
    if self.chunked:
        return self._readinto_chunked(b)
    if self.length is not None:
        if len(b) > self.length:
            # clip the read to the "end of response"
            b = memoryview(b)[0:self.length]
    # we do not use _safe_read() here because this may be a .will_close
    # connection, and the user is reading more bytes than will be provided
    # (for example, reading in 1k chunks)
    n = self.fp.readinto(b)
    if not n and b:
        # Ideally, we would raise IncompleteRead if the content-length
        # wasn't satisfied, but it might break compatibility.
        self._close_conn()
    elif self.length is not None:
        self.length -= n
        if not self.length:
            self._close_conn()
    return n

def _read_and_discard_trailer(self):
    # read and discard trailer up to the CRLF terminator
    ### note: we shouldn't have any trailers!
    while True:
        line = self.fp.readline(_MAXLINE + 1)
        if len(line) > _MAXLINE:
            raise LineTooLong("trailer line")
        if not line:
            # a vanishingly small number of sites EOF without
            # sending the trailer
            break
        if line in (b'\r\n', b'\n', b''):
            break

def read1(self, n=-1):
    """Read with at most one underlying system call.  If at least one
    byte is buffered, return that instead.
    """
    if self.fp is None or self._method == "HEAD":
        return b""
    if self.chunked:
        return self._read1_chunked(n)
    if self.length is not None and (n < 0 or n > self.length):
        n = self.length
    try:
        result = self.fp.read1(n)
    except ValueError:
        if n >= 0:
            raise
        # some implementations, like BufferedReader, don't support -1
        # Read an arbitrarily selected largeish chunk.
        result = self.fp.read1(16*1024)
    if not result and n:
        self._close_conn()
    elif self.length is not None:
        self.length -= len(result)
    return result

def _peek_chunked(self, n):
    # Strictly speaking, _get_chunk_left() may cause more than one read,
    # but that is ok, since that is to satisfy the chunked protocol.
    try:
        chunk_left = self._get_chunk_left()
    except IncompleteRead:
        return b'' # peek doesn't worry about protocol
    if chunk_left is None:
        return b'' # eof
    # peek is allowed to return more than requested.  Just request the
    # entire chunk, and truncate what we get.
    return self.fp.peek(chunk_left)[:chunk_left]

def getheader(self, name, default=None):
    '''Returns the value of the header matching *name*.
    If there are multiple matching headers, the values are
    combined into a single string separated by commas and spaces.
    If no matching header is found, returns *default* or None if
    the *default* is not specified.
    If the headers are unknown, raises http.client.ResponseNotReady.
    '''
    if self.headers is None:
        raise ResponseNotReady()
    headers = self.headers.get_all(name) or default
    if isinstance(headers, str) or not hasattr(headers, '__iter__'):
        return headers
    else:
        return ', '.join(headers)

def _is_textIO(stream):
    """Test whether a file-like object is a text or a binary stream.
    """
    return isinstance(stream, io.TextIOBase)

def _get_content_length(body, method):
    """Get the content-length based on the body.
    If the body is None, we set Content-Length: 0 for methods that expect
    a body (RFC 7230, Section 3.3.2). We also set the Content-Length for
    any method if the body is a str or bytes-like object and not a file.
    """
    if body is None:
        # do an explicit check for not None here to distinguish
        # between unset and set but empty
        if method.upper() in _METHODS_EXPECTING_BODY:
            return 0
        else:
            return None
    if hasattr(body, 'read'):
        # file-like object.
        return None
    try:
        # does it implement the buffer protocol (bytes, bytearray, array)?
        mv = memoryview(body)
        return mv.nbytes
    except TypeError:
        pass
    if isinstance(body, str):
        return len(body)
    return None

def send(self, data):
    """Send `data' to the server.
    ``data`` can be a string object, a bytes object, an array object, a
    file-like object that supports a .read() method, or an iterable object.
    """
    if self.sock is None:
        if self.auto_open:
            self.connect()
        else:
            raise NotConnected()
    if self.debuglevel > 0:
        print("send:", repr(data))
    blocksize = 8192
    if hasattr(data, "read") :
        if self.debuglevel > 0:
            print("sendIng a read()able")
        encode = False
        try:
            mode = data.mode
        except AttributeError:
            # io.BytesIO and other file-like objects don't have a `mode`
            # attribute.
            pass
        else:
            if "b" not in mode:
                encode = True
                if self.debuglevel > 0:
                    print("encoding file using iso-8859-1")
        while 1:
            datablock = data.read(blocksize)
            if not datablock:
                break
            if encode:
                datablock = datablock.encode("iso-8859-1")
            self.sock.sendall(datablock)
        return
    try:
        self.sock.sendall(data)
    except TypeError:
        if isinstance(data, collections.Iterable):
            for d in data:
                self.sock.sendall(d)
        else:
            raise TypeError("data should be a bytes-like object "
                            "or an iterable, got %r" % type(data))

def _set_content_length(self, body, method):
    # Set the content-length based on the body. If the body is "empty", we
    # set Content-Length: 0 for methods that expect a body (RFC 7230,
    # Section 3.3.2). If the body is set for other methods, we set the
    # header provided we can figure out what the length is.
    thelen = None
    method_expects_body = method.upper() in _METHODS_EXPECTING_BODY
    if body is None and method_expects_body:
        thelen = '0'
    elif body is not None:
        try:
            thelen = str(len(body))
        except TypeError:
            # If this is a file-like object, try to
            # fstat its file descriptor
            try:
                thelen = str(os.fstat(body.fileno()).st_size)
            except (AttributeError, OSError):
                # Don't send a length if this failed
                if self.debuglevel > 0: print("Cannot stat!!")
    if thelen is not None:
        self.putheader('Content-Length', thelen)

def join_header_words(lists):
    """Do the inverse (almost) of the conversion done by split_header_words.
    Takes a list of lists of (key, value) pairs and produces a single header
    value.  Attribute values are quoted if needed.
    >>> join_header_words([[("text/plain", None), ("charset", "iso-8859-1")]])
    'text/plain; charset="iso-8859-1"'
    >>> join_header_words([[("text/plain", None)], [("charset", "iso-8859-1")]])
    'text/plain, charset="iso-8859-1"'
    """
    headers = []
    for pairs in lists:
        attr = []
        for k, v in pairs:
            if v is not None:
                if not re.search(r"^\w+$", v):
                    v = HEADER_JOIN_ESCAPE_RE.sub(r"\\\1", v)  # escape " and \
                    v = '"%s"' % v
                k = "%s=%s" % (k, v)
            attr.append(k)
        if attr: headers.append("; ".join(attr))
    return ", ".join(headers)

def set_blocked_domains(self, blocked_domains):
    """Set the sequence of blocked domains."""
    self._blocked_domains = tuple(blocked_domains)

def allowed_domains(self):
    """Return None, or the sequence of allowed domains (as a tuple)."""
    return self._allowed_domains

def return_ok_verifiability(self, cookie, request):
    if request.unverifiable and is_third_party(request):
        if cookie.version > 0 and self.strict_rfc2965_unverifiable:
            _debug("   third-party RFC 2965 cookie during unverifiable "
                   "transaction")
            return False
        elif cookie.version == 0 and self.strict_ns_unverifiable:
            _debug("   third-party Netscape cookie during unverifiable "
                   "transaction")
            return False
    return True

def deepvalues(mapping):
    """Iterates over nested mapping, depth-first, in sorted order by key."""
    values = vals_sorted_by_key(mapping)
    for obj in values:
        mapping = False
        try:
            obj.items
        except AttributeError:
            pass
        else:
            mapping = True
            yield from deepvalues(obj)
        if not mapping:
            yield obj

def set_cookie(self, cookie):
    """Set a cookie, without checking whether or not it should be set."""
    c = self._cookies
    self._cookies_lock.acquire()
    try:
        if cookie.domain not in c: c[cookie.domain] = {}
        c2 = c[cookie.domain]
        if cookie.path not in c2: c2[cookie.path] = {}
        c3 = c2[cookie.path]
        c3[cookie.name] = cookie
    finally:
        self._cookies_lock.release()

def clear(self, domain=None, path=None, name=None):
    """Clear some cookies.
    Invoking this method without arguments will clear all cookies.  If
    given a single argument, only cookies belonging to that domain will be
    removed.  If given two arguments, cookies belonging to the specified
    path within that domain are removed.  If given three arguments, then
    the cookie with the specified name, path and domain is removed.
    Raises KeyError if no matching cookie exists.
    """
    if name is not None:
        if (domain is None) or (path is None):
            raise ValueError(
                "domain and path must be given to remove a cookie by name")
        del self._cookies[domain][path][name]
    elif path is not None:
        if domain is None:
            raise ValueError(
                "domain must be given to remove cookies by path")
        del self._cookies[domain][path]
    elif domain is not None:
        del self._cookies[domain]
    else:
        self._cookies = {}

def load(self, filename=None, ignore_discard=False, ignore_expires=False):
    """Load cookies from a file."""
    if filename is None:
        if self.filename is not None: filename = self.filename
        else: raise ValueError(MISSING_FILENAME_TEXT)
    with open(filename) as f:
        self._really_load(f, filename, ignore_discard, ignore_expires)

def lwp_cookie_str(cookie):
    """Return string representation of Cookie in the LWP cookie file format.
    Actually, the format is extended a bit -- see module docstring.
    """
    h = [(cookie.name, cookie.value),
         ("path", cookie.path),
         ("domain", cookie.domain)]
    if cookie.port is not None: h.append(("port", cookie.port))
    if cookie.path_specified: h.append(("path_spec", None))
    if cookie.port_specified: h.append(("port_spec", None))
    if cookie.domain_initial_dot: h.append(("domain_dot", None))
    if cookie.secure: h.append(("secure", None))
    if cookie.expires: h.append(("expires",
                               time2isoz(float(cookie.expires))))
    if cookie.discard: h.append(("discard", None))
    if cookie.comment: h.append(("comment", cookie.comment))
    if cookie.comment_url: h.append(("commenturl", cookie.comment_url))
    keys = sorted(cookie._rest.keys())
    for k in keys:
        h.append((k, str(cookie._rest[k])))
    h.append(("version", str(cookie.version)))
    return join_header_words([h])

def as_lwp_str(self, ignore_discard=True, ignore_expires=True):
    """Return cookies as a string of "\\n"-separated "Set-Cookie3" headers.
    ignore_discard and ignore_expires: see docstring for FileCookieJar.save
    """
    now = time.time()
    r = []
    for cookie in self:
        if not ignore_discard and cookie.discard:
            continue
        if not ignore_expires and cookie.is_expired(now):
            continue
        r.append("Set-Cookie3: %s" % lwp_cookie_str(cookie))
    return "\n".join(r+[""])

def __init__(self):
    # Set defaults
    self._key = self._value = self._coded_value = None
    # Set default attributes
    for key in self._reserved:
        dict.__setitem__(self, key, "")

def OutputString(self, attrs=None):
    # Build up our result
    #
    result = []
    append = result.append
    # First, the key=value pair
    append("%s=%s" % (self.key, self.coded_value))
    # Now add any defined attributes
    if attrs is None:
        attrs = self._reserved
    items = sorted(self.items())
    for key, value in items:
        if value == "":
            continue
        if key not in attrs:
            continue
        if key == "expires" and isinstance(value, int):
            append("%s=%s" % (self._reserved[key], _getdate(value)))
        elif key == "max-age" and isinstance(value, int):
            append("%s=%d" % (self._reserved[key], value))
        elif key in self._flags:
            if value:
                append(str(self._reserved[key]))
        else:
            append("%s=%s" % (self._reserved[key], value))
    # Return the result
    return _semispacejoin(result)

def __setitem__(self, key, value):
    """Dictionary style assignment."""
    if isinstance(value, Morsel):
        # allow assignment of constructed Morsels (e.g. for pickling)
        dict.__setitem__(self, key, value)
    else:
        rval, cval = self.value_encode(value)
        self.__set(key, rval, cval)

def load(self, rawdata):
    """Load cookies from a string (presumably HTTP_COOKIE) or
    from a dictionary.  Loading cookies from a dictionary 'd'
    is equivalent to calling:
        map(Cookie.__setitem__, d.keys(), d.values())
    """
    if isinstance(rawdata, str):
        self.__parse_string(rawdata)
    else:
        # self.update() wouldn't call our custom __setitem__
        for key, value in rawdata.items():
            self[key] = value
    return

def read(self, size):
    """Works like a blocking call to SSL_read(), whose behavior is
    described here:  http://www.openssl.org/docs/ssl/SSL_read.html"""
    if self.act_non_blocking:
        return self.fd.read(size)
    while True:
        try:
            return self.fd.read(size)
        except WantReadError:
            trampoline(self.fd.fileno(),
                       read=True,
                       timeout=self.gettimeout(),
                       timeout_exc=socket.timeout)
        except WantWriteError:
            trampoline(self.fd.fileno(),
                       write=True,
                       timeout=self.gettimeout(),
                       timeout_exc=socket.timeout)
        except SysCallError as e:
            if get_errno(e) == -1 or get_errno(e) > 0:
                return ''

def write(self, data):
    """Works like a blocking call to SSL_write(), whose behavior is
    described here:  http://www.openssl.org/docs/ssl/SSL_write.html"""
    if not data:
        return 0  # calling SSL_write() with 0 bytes to be sent is undefined
    if self.act_non_blocking:
        return self.fd.write(data)
    while True:
        try:
            return self.fd.write(data)
        except WantReadError:
            trampoline(self.fd.fileno(),
                       read=True,
                       timeout=self.gettimeout(),
                       timeout_exc=socket.timeout)
        except WantWriteError:
            trampoline(self.fd.fileno(),
                       write=True,
                       timeout=self.gettimeout(),
                       timeout_exc=socket.timeout)

def _remove_bad_fds(self):
    """ Iterate through fds, removing the ones that are bad per the
    operating system.
    """
    all_fds = list(self.listeners[self.READ]) + list(self.listeners[self.WRITE])
    for fd in all_fds:
        try:
            select.select([fd], [], [], 0)
        except select.error as e:
            if support.get_errno(e) in BAD_SOCK:
                self.remove_descriptor(fd)

def add(self, evtype, fileno, real_cb, real_tb, mac):
    # this is stupid: pyevent won't call a callback unless it's a function,
    # so we have to force it to be one here
    if isinstance(real_cb, types.BuiltinMethodType):
        def cb(_d):
            real_cb(_d)
    else:
        cb = real_cb
    if evtype is READ:
        evt = event.read(fileno, cb, fileno)
    elif evtype is WRITE:
        evt = event.write(fileno, cb, fileno)
    return super(Hub, self).add(evtype, fileno, evt, real_tb, mac)

def run(self, *a, **kw):
    """Run the runloop until abort is called.
    """
    # accept and discard variable arguments because they will be
    # supplied if other greenlets have run and exited before the
    # hub's greenlet gets a chance to run
    if self.running:
        raise RuntimeError("Already running!")
    try:
        self.running = True
        self.stopping = False
        while not self.stopping:
            while self.closed:
                # We ditch all of these first.
                self.close_one()
            self.prepare_timers()
            if self.debug_blocking:
                self.block_detect_pre()
            self.fire_timers(self.clock())
            if self.debug_blocking:
                self.block_detect_post()
            self.prepare_timers()
            wakeup_when = self.sleep_until()
            if wakeup_when is None:
                sleep_time = self.default_sleep()
            else:
                sleep_time = wakeup_when - self.clock()
            if sleep_time > 0:
                self.wait(sleep_time)
            else:
                self.wait(0)
        else:
            self.timers_canceled = 0
            del self.timers[:]
            del self.next_timers[:]
    finally:
        self.running = False
        self.stopping = False

def get_hub():
    """Get the current event hub singleton object.
    .. note :: |internal|
    """
    try:
        hub = _threadlocal.hub
    except AttributeError:
        try:
            _threadlocal.Hub
        except AttributeError:
            use_hub()
        hub = _threadlocal.hub = _threadlocal.Hub()
    return hub

def __del__(self):
    # This is in case self.close is not assigned yet (currently the constructor does it)
    close = getattr(self, 'close', None)
