def test_AI_ADDRCONFIG_noaddr(self):
    # If AI_ADDRCONFIG is used but there is no address we need to
    # get an exception, not an empty list.
    def getaddrinfo(addr, port, family, socktype, proto, aiflags):
        raise socket.error(socket.EAI_ADDRFAMILY,
                           'Address family for hostname not supported')
    greendns.socket.getaddrinfo = getaddrinfo
    greendns.resolve = _make_mock_resolve()
    try:
        greendns.getaddrinfo('::1', None, 0, 0, 0, socket.AI_ADDRCONFIG)
    except socket.error as e:
        assert e.errno == socket.EAI_ADDRFAMILY

def spawn_order_check(self, concurrency):
    # checks that piles are strictly ordered
    p = eventlet.GreenPile(concurrency)
    def makework(count, unique):
        for i in six.moves.range(count):
            token = (unique, i)
            p.spawn(pressure, token)
    iters = 1000
    eventlet.spawn(makework, iters, 1)
    eventlet.spawn(makework, iters, 2)
    eventlet.spawn(makework, iters, 3)
    p.spawn(pressure, (0, 0))
    latest = [-1] * 4
    received = 0
    it = iter(p)
    while True:
        try:
            i = six.next(it)
        except StressException as exc:
            i = exc.args[0]
        except StopIteration:
            break
        received += 1
        if received % 5 == 0:
            eventlet.sleep(0.0001)
        unique, order = i
        assert latest[unique] < order
        latest[unique] = order
    for l in latest[1:]:
        self.assertEqual(l, iters - 1)

