def send_expect_close(sock, buf):
    # Some tests will induce behavior that causes the remote end to
    # close the connection before all of the data has been written.
    # With small kernel buffer sizes, this can cause an EPIPE error.
    # Since the test expects an early close, this can be ignored.
    try:
        sock.sendall(buf)
    except socket.error as exc:
        if support.get_errno(exc) != errno.EPIPE:
            raise

def skip_if(condition):
    """ Decorator that skips a test if the *condition* evaluates True.
    *condition* can be a boolean or a callable that accepts one argument.
    The callable will be called with the function to be decorated, and
    should return True to skip the test.
    """
    def skipped_wrapper(func):
        @functools.wraps(func)
        def wrapped(*a, **kw):
            if isinstance(condition, bool):
                result = condition
            else:
                result = condition(func)
            if result:
                raise SkipTest()
            else:
                return func(*a, **kw)
        return wrapped
    return skipped_wrapper

def skip_on_windows(func):
    """ Decorator that skips a test on Windows."""
    return skip_if(sys.platform.startswith('win'))(func)

def skip_if_CRLock_exist(func):
    """ Decorator that skips a test if the `_thread.RLock` class exists """
    try:
        from _thread import RLock
        return skipped(func)
    except ImportError:
        return func

def get_database_auth():
    """Retrieves a dict of connection parameters for connecting to test databases.
    Authentication parameters are highly-machine specific, so
    get_database_auth gets its information from either environment
    variables or a config file.  The environment variable is
    "EVENTLET_DB_TEST_AUTH" and it should contain a json object.  If
    this environment variable is present, it's used and config files
    are ignored.  If it's not present, it looks in the local directory
    (tests) and in the user's home directory for a file named
    ".test_dbauth", which contains a json map of parameters to the
    connect function.
    """
    retval = {
        'MySQLdb': {'host': 'localhost', 'user': 'root', 'passwd': ''},
        'psycopg2': {'user': 'test'},
    }
    if 'EVENTLET_DB_TEST_AUTH' in os.environ:
        return json.loads(os.environ.get('EVENTLET_DB_TEST_AUTH'))
    files = [os.path.join(os.path.dirname(__file__), '.test_dbauth'),
             os.path.join(os.path.expanduser('~'), '.test_dbauth')]
    for f in files:
        try:
            auth_utf8 = json.load(open(f))
            # Have to convert unicode objects to str objects because
            # mysqldb is dumb. Using a doubly-nested list comprehension
            # because we know that the structure is a two-level dict.
            return dict(
                [(str(modname), dict(
                    [(str(k), str(v)) for k, v in connectargs.items()]))
                 for modname, connectargs in auth_utf8.items()])
        except IOError:
            pass
    return retval

def assert_raises(exc):
    """exc is an exception class"""
    try:
        yield
    except exc:
        pass
    else:
        raise AssertionError("failed to raise expected exception {0}"
                             .format(exc.__class__.__name__))

def _fetch_containers(self, **fetch_options):
    # Account for containers that might have been removed since we fetched
    # the list.
    def soft_inspect(container):
        try:
            return Container.from_id(self.client, container['Id'])
        except NotFound:
            return None
    return filter(None, [
        soft_inspect(container)
        for container in self.client.containers(**fetch_options)
    ])

def warn_on_masked_volume(volumes_option, container_volumes, service):
    container_volumes = {
        volume.internal: volume.external
        for volume in container_volumes}
    for volume in volumes_option:
        if (
                volume.external and
                volume.internal in container_volumes and
                container_volumes.get(volume.internal) != volume.external
        ):
            log.warning((
                "Service \"{service}\" is using volume \"{volume}\" from the "
                "previous container. Host mapping \"{host_path}\" has no effect. "
                "Remove the existing containers (with `docker-compose rm {service}`) "
                "to use the host volume mapping."
            ).format(
                service=service,
                volume=volume.internal,
                host_path=volume.external))

def build_container_labels(label_options, service_labels, number, config_hash, slug):
    labels = dict(label_options or {})
    labels.update(label.split('=', 1) for label in service_labels)
    if number is not None:
        labels[LABEL_CONTAINER_NUMBER] = str(number)
    if slug is not None:
        labels[LABEL_SLUG] = slug
    labels[LABEL_VERSION] = __version__
    if config_hash:
        log.debug("Added config hash: %s" % config_hash)
        labels[LABEL_CONFIG_HASH] = config_hash
    return labels

def stream_as_text(stream):
    """Given a stream of bytes or text, if any of the items in the stream
    are bytes convert them to text.
    This function can be removed once docker-py returns text streams instead
    of byte streams.
    """
    for data in stream:
        if not isinstance(data, str):
            data = data.decode('utf-8', 'replace')
        yield data

def split_buffer(stream, splitter=None, decoder=lambda a: a):
    """Given a generator which yields strings and a splitter function,
    joins all input, splits on the separator and yields each chunk.
    Unlike string.split(), each chunk includes the trailing
    separator, except for the last one if none was found on the end
    of the input.
    """
    splitter = splitter or line_splitter
    buffered = ''
    for data in stream_as_text(stream):
        buffered += data
        while True:
            buffer_split = splitter(buffered)
            if buffer_split is None:
                break
            item, buffered = buffer_split
            yield item
    if buffered:
        try:
            yield decoder(buffered)
        except Exception as e:
            log.error(
                'Compose tried decoding the following data chunk, but failed:'
                '\n%s' % repr(buffered)
            )
            raise StreamParseError(e)

def json_stream(stream):
    """Given a stream of text, return a stream of json objects.
    This handles streams which are inconsistently buffered (some entries may
    be newline delimited, and others are not).
    """
    return split_buffer(stream, json_splitter, json_decoder.decode)

def get_services_without_duplicate(self, service_names=None, include_deps=False):
    services = self.get_services(service_names, include_deps)
    for service in services:
        service.remove_duplicate_containers()
    return services

def _legacy_event_processor(self, service_names):
    # Only for v1 files or when Compose is forced to use an older API version
    def build_container_event(event, container):
        time = datetime.datetime.fromtimestamp(event['time'])
        time = time.replace(
            microsecond=microseconds_from_time_nano(event['timeNano'])
        )
        return {
            'time': time,
            'type': 'container',
            'action': event['status'],
            'id': container.id,
            'service': container.service,
            'attributes': {
                'name': container.name,
                'image': event['from'],
            },
            'container': container,
        }
    service_names = set(service_names or self.service_names)
    for event in self.client.events(
        filters={'label': self.labels()},
        decode=True
    ):
        # This is a guard against some events broadcasted by swarm that
        # don't have a status field.
        # See https://github.com/docker/compose/issues/3316
        if 'status' not in event:
            continue
        try:
            # this can fail if the container has been removed or if the event
            # refers to an image
            container = Container.from_id(self.client, event['id'])
        except APIError:
            continue
        if container.service not in service_names:
            continue
        yield build_container_event(event, container)

def human_readable_health_status(self):
    """ Generate UP status string with up time and health
    """
    status_string = 'Up'
    container_status = self.get('State.Health.Status')
    if container_status == 'starting':
        status_string += ' (health: starting)'
