def init_env():
    """Initialize blueoil environment"""
    blueoil_dir = str(Path('{}/../../../'.format(__file__)).resolve())
    config_dir = os.path.join(blueoil_dir, 'tests/fixtures/configs')
    train_output_dir = os.path.join(blueoil_dir, 'tmp')
    predict_output_dir = tempfile.TemporaryDirectory()
    environment_originals = {}
    environ_originals = {}
    # TODO: Remove this setting after blueoil.environment has been refactored.
    envs = {
        "DATA_DIR": os.path.join(blueoil_dir, "tests", "unit", "fixtures", "datasets"),
        "OUTPUT_DIR": train_output_dir,
        "_EXPERIMENT_DIR": os.path.join(train_output_dir, "{experiment_id}"),
        "_TENSORBOARD_DIR": os.path.join(train_output_dir, "{experiment_id}", "tensorboard"),
        "_CHECKPOINTS_DIR": os.path.join(train_output_dir, "{experiment_id}", "checkpoints"),
    }
    for k, v in envs.items():
        environment_originals[k] = getattr(environment, k)
        environ_originals[k] = os.environ.get(k)
        setattr(environment, k, v)
        os.environ[k] = v
    yield {
        "train_output_dir": train_output_dir,
        "predict_output_dir": predict_output_dir.name,
        "blueoil_dir": blueoil_dir,
        "config_dir": config_dir,
    }
    for k, v in environment_originals.items():
        setattr(environment, k, v)
    for k, v in environ_originals.items():
        if v is not None:
            os.environ[k] = v
        else:
            del os.environ[k]
    predict_output_dir.cleanup()

def all_next(self, ret_type=None):
    '''Generator of all consecutive dates. Can be used instead of
    implicit call to __iter__, whenever non-default
    'ret_type' has to be specified.
    '''
    # In a Python 3.7+ world:  contextlib.supress and contextlib.nullcontext could be used instead
    try:
        while True:
            self._is_prev = False
            yield self._get_next(ret_type or self._ret_type)
    except CroniterBadDateError:
        if self._max_years_btw_matches_explicitly_set:
            return
        else:
            raise

def _get_nth_weekday_of_month(year, month, day_of_week):
    """ For a given year/month return a list of days in nth-day-of-month order.
    The last weekday of the month is always [-1].
    """
    w = (day_of_week + 6) % 7
    c = calendar.Calendar(w).monthdayscalendar(year, month)
    if c[0][0] == 0:
        c.pop(0)
    return tuple(i[0] for i in c)

def expand(cls, expr_format, hash_id=None):
    """Shallow non Croniter ValueError inside a nice CroniterBadCronError"""
    try:
        return cls._expand(expr_format, hash_id=hash_id)
    except ValueError as exc:
        error_type, error_instance, traceback = sys.exc_info()
        if isinstance(exc, CroniterError):
            raise
        if int(sys.version[0]) >= 3:
            globs, locs = _get_caller_globals_and_locals()
            exec("raise CroniterBadCronError from  exc", globs, locs)
        else:
            raise CroniterBadCronError("{0}".format(exc))

