def test_croniter_range_derived_class(self):
    # trivial example extending croniter
    class croniter_nosec(croniter):
        """ Like croniter, but it forbids second-level cron expressions. """
        @classmethod
        def expand(cls, expr_format, *args, **kwargs):
            if len(expr_format.split()) == 6:
                raise CroniterBadCronError("Expected 'min hour day mon dow'")
            return croniter.expand(expr_format, *args, **kwargs)
    cron = "0 13 8 1,4,7,10 wed"
    matches = list(croniter_range(datetime(2020, 1, 1), datetime(2020, 12, 31), cron, day_or=False, _croniter=croniter_nosec))
    self.assertEqual(len(matches), 3)
    cron = "0 1 8 1,15,L wed 15,45"
    with self.assertRaises(CroniterBadCronError):
        # Should fail using the custom class that forbids the seconds expression
        croniter_nosec(cron)
    with self.assertRaises(CroniterBadCronError):
        # Should similarly fail because the custom class rejects seconds expr
        i = croniter_range(datetime(2020, 1, 1), datetime(2020, 12, 31), cron, _croniter=croniter_nosec)
        next(i)

def __init__(self, page, kwargs):
    self.page = page
    self.kwargs = kwargs

def run(self):
    """Main routine.
    :return:
    """
    while not self._stopped.is_set():
        try:
            message = self.q.get(timeout=0.1)
        except Empty:
            continue
        dtype, data, ts = message
        if dtype in ('subscribed', 'unsubscribed', 'conf', 'auth', 'unauth'):
            try:
                self._response_handlers[dtype](dtype, data, ts)
            except KeyError:
                self.log.error("Dtype '%s' does not have a response "
                               "handler! (%s)", dtype, message)
        elif dtype == 'data':
            try:
                channel_id = data[0]
                if channel_id != 0:
                    # Get channel type associated with this data to the
                    # associated data type (from 'data' to
                    # 'book', 'ticker' or similar
                    channel_type, *_ = self.channel_directory[channel_id]
                    # Run the associated data handler for this channel type.
                    self._data_handlers[channel_type](channel_type, data, ts)
                    # Update time stamps.
                    self.update_timestamps(channel_id, ts)
                else:
                    # This is data from auth channel, call handler
                    self._handle_account(data=data, ts=ts)
            except KeyError:
                self.log.error("Channel ID does not have a data handler! %s",
                               message)
        else:
            self.log.error("Unknown dtype on queue! %s", message)
            continue

def reset(self):
    """Reset the client.
    :return:
    """
    self.conn.reconnect()
    while not self.conn.connected.is_set():
        log.info("reset(): Waiting for connection to be set up..")
        time.sleep(1)
    for key in self.channel_configs:
        self.conn.send(**self.channel_configs[key])

def authenticate(self):
    """Authenticate with the Bitfinex API.
    :return:
    """
    if not self.key and not self.secret:
        raise ValueError("Must supply both key and secret key for API!")
    self.channel_configs['auth'] = {'api_key': self.key, 'secret': self.secret}
    self.conn.send(api_key=self.key, secret=self.secret, auth=True)

def sort_dataframe_result(df, result: pd.DataFrame) -> pd.DataFrame:
    """ sort DataFrame on client according to `should_be_monotonic` attribute """
    if hasattr(df, 'index_value'):
        if getattr(df.index_value, 'should_be_monotonic', False):
            try:
                result.sort_index(inplace=True)
            except TypeError:  # pragma: no cover
                # cudf doesn't support inplace
                result = result.sort_index()
        if hasattr(df, 'columns_value'):
            if getattr(df.columns_value, 'should_be_monotonic', False):
                try:
                    result.sort_index(axis=1, inplace=True)
                except TypeError:  # pragma: no cover
                    # cudf doesn't support inplace
                    result = result.sort_index(axis=1)
    return result

def has_unknown_shape(*tiled_tileables: TileableType) -> bool:
    for tileable in tiled_tileables:
        if getattr(tileable, 'shape', None) is None:
            continue
        if any(pd.isnull(s) for s in tileable.shape):
            return True
        if any(pd.isnull(s) for s in itertools.chain(*tileable.nsplits)):
            return True
    return False

def replace_objects(nested: Union[List, Dict],
                    mapping: Dict) -> Union[List, Dict]:
    if not mapping:
        return nested
    if isinstance(nested, dict):
        vals = list(nested.values())
    else:
        vals = list(nested)
    new_vals = []
    for val in vals:
        if isinstance(val, (dict, list, tuple, set)):
            new_val = replace_objects(val, mapping)
        else:
            try:
                new_val = mapping.get(val, val)
            except TypeError:
                new_val = val
        new_vals.append(new_val)
    if isinstance(nested, dict):
        return type(nested)((k, v) for k, v in zip(nested.keys(), new_vals))
    else:
        return type(nested)(new_vals)

